import React from 'react';

const Hero: React.FC = () => {
  return (
    <section style={{ display: 'flex', alignItems: 'center', padding: '2rem', backgroundColor: '#f5f5f5' }}>
      <img
        src="https://cdn.pixabay.com/photo/2017/06/09/07/37/notebook-2386034_1280.jpg"
        alt="Profile"
        style={{ width: '200px', height: '200px', borderRadius: '50%', marginRight: '2rem', objectFit: 'cover' }}
      />
      <div>
        <h1 style={{ margin: 0, fontSize: '2.5rem' }}>Welcome to My Portfolio</h1>
        <p style={{ fontSize: '1.25rem', color: '#555' }}>
          Hi! I'm a passionate developer excited to share my projects and skills with you.
        </p>
      </div>
    </section>
  );
};

export default Hero;
